pragma solidity 0.8.12;

interface IScaledBalanceToken {
	/**
	 * @dev Returns the scaled balance of the user. The scaled balance is the sum of all the
	 * updated stored balance divided by the reserve's liquidity index at the moment of the update
	 * @param user The user whose balance is calculated
	 * @return The scaled balance of the user
	 **/
	function scaledBalanceOf(address user) external view returns (uint256);

	/**
	 * @dev Returns the scaled balance of the user and the scaled total supply.
	 * @param user The address of the user
	 * @return The scaled balance of the user
	 * @return The scaled balance and the scaled total supply
	 **/
	function getScaledUserBalanceAndSupply(address user) external view returns (uint256, uint256);

	/**
	 * @dev Returns the scaled total supply of the variable debt token. Represents sum(debt/index)
	 * @return The scaled total supply
	 **/
	function scaledTotalSupply() external view returns (uint256);
}
","keccak256":"0x20a9147e059d362660996b4922825860e759019f4cbbf293d3f4f2ccc7dadc57","license":"agpl-3.0"},"contracts/interfaces/IVariableDebtToken.sol":{"content":"// SPDX-License-Identifier: agpl-3.0
pragma solidity 0.8.12;

import {IScaledBalanceToken} from "./IScaledBalanceToken.sol";
import {IInitializableDebtToken} from "./IInitializableDebtToken.sol";
import {IAaveIncentivesController} from "./IAaveIncentivesController.sol";

/**
 * @title IVariableDebtToken
 * @author Aave
 * @notice Defines the basic interface for a variable debt token.
 **/
interface IVariableDebtToken is IScaledBalanceToken, IInitializableDebtToken {
	/**
	 * @dev Emitted after the mint action
	 * @param from The address performing the mint
	 * @param onBehalfOf The address of the user on which behalf minting has been performed
	 * @param value The amount to be minted
	 * @param index The last index of the reserve
	 **/
	event Mint(address indexed from, address indexed onBehalfOf, uint256 value, uint256 index);

	/**
	 * @dev Mints debt token to the `onBehalfOf` address
	 * @param user The address receiving the borrowed underlying, being the delegatee in case
	 * of credit delegate, or same as `onBehalfOf` otherwise
	 * @param onBehalfOf The address receiving the debt tokens
	 * @param amount The amount of debt being minted
	 * @param index The variable debt index of the reserve
	 * @return `true` if the the previous balance of the user is 0
	 **/
	function mint(address user, address onBehalfOf, uint256 amount, uint256 index) external returns (bool);

	/**
	 * @dev Emitted when variable debt is burnt
	 * @param user The user which debt has been burned
	 * @param amount The amount of debt being burned
	 * @param index The index of the user
	 **/
	event Burn(address indexed user, uint256 amount, uint256 index);

	/**
	 * @dev Burns user variable debt
	 * @param user The user which debt is burnt
	 * @param index The variable debt index of the reserve
	 **/
	function burn(address user, uint256 amount, uint256 index) external;

	/**
	 * @dev Returns the address of the incentives controller contract
	 **/
	function getIncentivesController() external view returns (IAaveIncentivesController);
}
","keccak256":"0x45c38c0244862161745e30c52156a7d37ccce31d20eded4ee6538696ccec7aab","license":"agpl-3.0"},"contracts/lending/libraries/aave-upgradeability/VersionedInitializable.sol":{"content":"// SPDX-License-Identifier: agpl-3.0
pragma solidity 0.8.12;

/**
 * @title VersionedInitializable
 *
 * @dev Helper contract to implement initializer functions. To use it, replace
 * the constructor with a function that has the `initializer` modifier.
 * WARNING: Unlike constructors, initializer functions must be manually
 * invoked. This applies both to deploying an Initializable contract, as well
 * as extending an Initializable contract via inheritance.
 * WARNING: When used with inheritance, manual care must be taken to not invoke
 * a parent initializer twice, or ensure that all initializers are idempotent,
 * because this is not dealt with automatically as with constructors.
 *
 * @author Aave, inspired by the OpenZeppelin Initializable contract
 */
abstract contract VersionedInitializable {

	/**
	 * @dev Indicates that the contract has been initialized.
	 */
	uint256 private lastInitializedRevision = 0;

	/**
	 * @dev Indicates that the contract is in the process of being initialized.
	 */
	bool private initializing;

	/**
	 * @dev Indicates that the contract has been initialized.
	 */
	bool private initialized;

	/**
	 * @dev Modifier to use in the initializer function of a contract.
	 */
	modifier initializer() {
		uint256 revision = getRevision();
		bool isTopLevelCall = !initializing;

		require(
			isTopLevelCall && (revision > lastInitializedRevision || !initialized),
			"Contract instance has already been initialized"
		);

		if (isTopLevelCall) {
			initializing = true;
			initialized = true;
			lastInitializedRevision = revision;
		}

		_;

		if (isTopLevelCall) {
			initializing = false;
		}
	}

	/**
	 * @dev returns the revision number of the contract
	 * Needs to be defined in the inherited class as a constant.
	 **/
	function getRevision() internal pure virtual returns (uint256);

	function _disableInitializers() internal virtual {
		require(!initializing, "Initializable: contract is initializing");
		if (!initialized) {
			initialized = true;
		}
	}

	// Reserved storage space to allow for layout changes in the future.
	uint256[50] private ______gap;
}
