// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import {OFTV2} from "@layerzerolabs/solidity-examples/contracts/token/oft/v2/OFTV2.sol";
import {Pausable} from "@openzeppelin/contracts/security/Pausable.sol";
import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";

import {IPriceProvider} from "../../interfaces/IPriceProvider.sol";

/// @title Prime token contract with OFT integration
/// @author Prime Devs
contract PrimeToken is OFTV2, Pausable, ReentrancyGuard {
	/// @notice bridge fee reciever
	address private treasury;

	/// @notice Fee ratio for bridging, in bips
	uint256 public feeRatio;

	/// @notice Divisor for fee ratio, 100%
	uint256 public constant FEE_DIVISOR = 10000;

	/// @notice Max reasonable fee, 1%
	uint256 public constant MAX_REASONABLE_FEE = 100;

	/// @notice PriceProvider, for PRNT price in native fee calc
	IPriceProvider public priceProvider;

	/// @notice Decimals for OFTV2
	uint8 public constant SHARED_DECIMALS = 8;

	/// @notice Emitted when fee ratio is updated
	event FeeRatioUpdated(uint256 indexed fee);

	/// @notice Emitted when PriceProvider is updated
	event PriceProviderUpdated(IPriceProvider indexed priceProvider);

	/// @notice Emitted when Treasury is updated
	event TreasuryUpdated(address indexed treasury);

	error AmountTooSmall();

	/// @notice Error message emitted when the provided ETH does not cover the bridge fee
	error InsufficientETHForFee();

	/// @notice Emitted when null address is set
	error AddressZero();

	/// @notice Emitted when ratio is invalid
	error InvalidRatio();

	/**
	 * @notice Create PrimeOFT
	 * @param _tokenName token name
	 * @param _symbol token symbol
	 * @param _endpoint LZ endpoint for network
	 * @param _dao DAO address, for initial mint
	 * @param _treasury Treasury address, for fee recieve
	 * @param _mintAmt Mint amount
	 */
	constructor(
		string memory _tokenName,
		string memory _symbol,
		address _endpoint,
		address _dao,
		address _treasury,
		uint256 _mintAmt
	) OFTV2(_tokenName, _symbol, SHARED_DECIMALS, _endpoint) {
		if (_endpoint == address(0)) revert AddressZero();
		if (_dao == address(0)) revert AddressZero();
		if (_treasury == address(0)) revert AddressZero();

		treasury = _treasury;

		if (_mintAmt != 0) {
			_mint(_dao, _mintAmt);
		}
	}

	/**
	 * @notice Burn tokens.
	 * @param _amount to burn
	 */
	function burn(uint256 _amount) public {
		_burn(_msgSender(), _amount);
	}

	/**
	 * @notice Pause bridge operation.
	 */
	function pause() public onlyOwner {
		_pause();
	}

	/**
	 * @notice Unpause bridge operation.
	 */
	function unpause() public onlyOwner {
		_unpause();
	}

	/**
	 * @notice Returns LZ fee + Bridge fee
	 * @dev overrides default OFT estimate fee function to add native fee
	 * @param _dstChainId dest LZ chain id
	 * @param _toAddress to addr on dst chain
	 * @param _amount amount to bridge
	 * @param _useZro use ZRO token, someday ;)
	 * @param _adapterParams LZ adapter params
	 */
	function estimateSendFee(
		uint16 _dstChainId,
		bytes32 _toAddress,
		uint256 _amount,
		bool _useZro,
		bytes calldata _adapterParams
	) public view override returns (uint256 nativeFee, uint256 zroFee) {
		(nativeFee, zroFee) = super.estimateSendFee(_dstChainId, _toAddress, _amount, _useZro, _adapterParams);
		nativeFee = nativeFee + getBridgeFee(_amount);
	}

	function _updatePrice() internal {
		if (address(priceProvider) != address(0)) {
			priceProvider.update();
		}
	}

	/**
	 * @notice Returns amount after dust
	 * @dev overrides default OFT _send function to add native fee
	 * @param _from from addr
	 * @param _dstChainId dest LZ chain id
	 * @param _toAddress to addr on dst chain
	 * @param _amount amount to bridge
	 * @param _refundAddress refund addr
	 * @param _zroPaymentAddress use ZRO token, someday ;)
	 * @param _adapterParams LZ adapter params
	 */
	function _send(
		address _from,
		uint16 _dstChainId,
		bytes32 _toAddress,
		uint256 _amount,
		address payable _refundAddress,
		address _zroPaymentAddress,
		bytes memory _adapterParams
	) internal override nonReentrant whenNotPaused returns (uint256 amount) {
		_updatePrice();

		(amount, ) = _removeDust(_amount);
		uint256 fee = getBridgeFee(amount);
		if (msg.value < fee) revert InsufficientETHForFee();

		_checkAdapterParams(_dstChainId, PT_SEND, _adapterParams, NO_EXTRA_GAS);

		if (amount == 0) revert AmountTooSmall();
		_debitFrom(_from, _dstChainId, _toAddress, amount); // amount returned should not have dust

		bytes memory lzPayload = _encodeSendPayload(_toAddress, _ld2sd(amount));
		_lzSend(_dstChainId, lzPayload, _refundAddress, _zroPaymentAddress, _adapterParams, msg.value - fee);

		if (fee > 0) {
			Address.sendValue(payable(treasury), fee);
		}

		emit SendToChain(_dstChainId, _from, _toAddress, amount);
	}

	/**
	 * @notice Bridge token and execute calldata on destination chain
	 * @dev overrides default OFT _sendAndCall function to add native fee
	 * @param _from from addr
	 * @param _dstChainId dest LZ chain id
	 * @param _toAddress to addr on dst chain
	 * @param _amount amount to bridge
	 * @param _payload calldata to execute on dst chain
	 * @param _dstGasForCall amount of gas to use on dst chain
	 * @param _refundAddress refund addr
	 * @param _zroPaymentAddress use ZRO token, someday ;)
	 * @param _adapterParams LZ adapter params
	 */
	function _sendAndCall(
		address _from,
		uint16 _dstChainId,
		bytes32 _toAddress,
		uint _amount,
		bytes memory _payload,
		uint64 _dstGasForCall,
		address payable _refundAddress,
		address _zroPaymentAddress,
		bytes memory _adapterParams
	) internal override nonReentrant whenNotPaused returns (uint amount) {
		_updatePrice();

		(amount, ) = _removeDust(_amount);
		uint256 fee = getBridgeFee(amount);
		if (msg.value < fee) revert InsufficientETHForFee();

		_checkAdapterParams(_dstChainId, PT_SEND_AND_CALL, _adapterParams, _dstGasForCall);

		if (amount == 0) revert AmountTooSmall();
		_debitFrom(_from, _dstChainId, _toAddress, amount);

		// encode the msg.sender into the payload instead of _from
		bytes memory lzPayload = _encodeSendAndCallPayload(
			msg.sender,
			_toAddress,
			_ld2sd(amount),
			_payload,
			_dstGasForCall
		);
		_lzSend(_dstChainId, lzPayload, _refundAddress, _zroPaymentAddress, _adapterParams, msg.value - fee);

		if (fee > 0) {
			Address.sendValue(payable(treasury), fee);
		}

		emit SendToChain(_dstChainId, _from, _toAddress, amount);
	}

	/**
	 * @notice overrides default OFT _debitFrom function to make pauseable
	 * @param _from from addr
	 * @param _dstChainId dest LZ chain id
	 * @param _toAddress to addr on dst chain
	 * @param _amount amount to bridge
	 */
	function _debitFrom(
		address _from,
		uint16 _dstChainId,
		bytes32 _toAddress,
		uint256 _amount
	) internal override whenNotPaused returns (uint256) {
		return super._debitFrom(_from, _dstChainId, _toAddress, _amount);
	}

	/**
	 * @notice Bridge fee amount
	 * @param _prntAmount amount for bridge
	 * @return bridgeFee calculated bridge fee
	 */
	function getBridgeFee(uint256 _prntAmount) public view returns (uint256 bridgeFee) {
		if (address(priceProvider) == address(0)) {
			return 0;
		}
		uint256 priceInEth = priceProvider.getTokenPrice();
		uint256 priceDecimals = priceProvider.decimals();
		uint256 prntInEth = _prntAmount * priceInEth * (10 ** 18) / (10 ** priceDecimals) / (10 ** decimals());
		bridgeFee = (prntInEth * feeRatio) / FEE_DIVISOR;
	}

	/**
	 * @notice Set fee info
	 * @param _feeRatio ratio
	 */
	function setFeeRatio(uint256 _feeRatio) external onlyOwner {
		if (_feeRatio > MAX_REASONABLE_FEE) revert InvalidRatio();
		feeRatio = _feeRatio;
		emit FeeRatioUpdated(_feeRatio);
	}

	/**
	 * @notice Set price provider
	 * @param _priceProvider address
	 */
	function setPriceProvider(IPriceProvider _priceProvider) external onlyOwner {
		if (address(_priceProvider) == address(0)) revert AddressZero();
		priceProvider = _priceProvider;
		emit PriceProviderUpdated(_priceProvider);
	}

	/**
	 * @notice Set Treasury
	 * @param _treasury address
	 */
	function setTreasury(address _treasury) external onlyOwner {
		if (_treasury == address(0)) revert AddressZero();
		treasury = _treasury;
		emit TreasuryUpdated(_treasury);
	}
}

contracts/prime/zap/helpers/DustRefunder.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import {IWETH} from "../../../interfaces/IWETH.sol";

/// @title Dust Refunder Contract
/// @dev Refunds dust tokens remaining from zapping.
/// @author Prime
contract DustRefunder {
	using SafeERC20 for IERC20;

	/**
	 * @notice Refunds PRNT and WETH.
	 * @param _prnt PRNT address
	 * @param _weth WETH address
	 * @param _refundAddress Address for refund
	 */
	function _refundDust(address _prnt, address _weth, address _refundAddress) internal {
		IERC20 prnt = IERC20(_prnt);
		IWETH weth = IWETH(_weth);

		uint256 dustWETH = weth.balanceOf(address(this));
		if (dustWETH > 0) {
			weth.transfer(_refundAddress, dustWETH);
		}
		uint256 dustPrnt = prnt.balanceOf(address(this));
		if (dustPrnt > 0) {
			prnt.safeTransfer(_refundAddress, dustPrnt);
		}
	}
}

contracts/prime/zap/helpers/UniswapPoolH.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import {DustRefunder} from "./DustRefunder.sol";
import {UniswapV2Library} from "../../../uniswap-solc-0.8/libraries/UniswapV2Library.sol";
import {IUniswapV2Pair} from "../../../uniswap-solc-0.8/interfaces/IUniswapV2Pair.sol";
import {IUniswapV2Factory} from "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {HomoraMath} from "../../../dependencies/math/HomoraMath.sol";
import {IUniswapV2Router02} from "../../../interfaces/uniswap/IUniswapV2Router02.sol";
import {ILiquidityZap} from "../../../interfaces/ILiquidityZap.sol";
import {IWETH} from "../../../interfaces/IWETH.sol";

/// @title Uniswap Pool Helper Contract
/// @author Prime
contract UniswapPoolHelper is Initializable, OwnableUpgradeable, DustRefunder {
	using SafeERC20 for IERC20;
	using HomoraMath for uint256;

	/********************** Events ***********************/
	event LiquidityZapUpdated(address indexed _liquidityZap);

	event LockZapUpdated(address indexed _lockZap);

	/********************** Errors ***********************/
	error AddressZero();
	error InsufficientPermission();

	address public lpTokenAddr;
	address public prntAddr;
	address public wethAddr;

	IUniswapV2Router02 public router;
	ILiquidityZap public liquidityZap;
	address public lockZap;

	constructor() {
		_disableInitializers();
	}

	/**
	 * @notice Initializer
	 * @param _prntAddr PRNT address
	 * @param _wethAddr WETH address
	 * @param _routerAddr Uniswap router address
	 * @param _liquidityZap LiquidityZap addrress
	 */
	function initialize(
		address _prntAddr,
		address _wethAddr,
		address _routerAddr,
		ILiquidityZap _liquidityZap
	) external initializer {
		if (_prntAddr == address(0)) revert AddressZero();
		if (_wethAddr == address(0)) revert AddressZero();
		if (_routerAddr == address(0)) revert AddressZero();
		if (address(_liquidityZap) == address(0)) revert AddressZero();

		__Ownable_init();

		prntAddr = _prntAddr;
		wethAddr = _wethAddr;

		router = IUniswapV2Router02(_routerAddr);
		liquidityZap = _liquidityZap;
	}

	/**
	 * @notice Initialize PRNT/WETH pool and liquidity zap
	 */
	function initializePool() public onlyOwner {
		lpTokenAddr = IUniswapV2Factory(router.factory()).createPair(prntAddr, wethAddr);

		IERC20 prnt = IERC20(prntAddr);
		prnt.forceApprove(address(router), type(uint256).max);
		prnt.forceApprove(address(liquidityZap), type(uint256).max);
		IERC20(wethAddr).approve(address(liquidityZap), type(uint256).max);
		IERC20(wethAddr).approve(address(router), type(uint256).max);

		router.addLiquidity(
			address(prnt),
			wethAddr,
			prnt.balanceOf(address(this)),
			IERC20(wethAddr).balanceOf(address(this)),
			0,
			0,
			address(this),
			block.timestamp
		);

		IERC20 lp = IERC20(lpTokenAddr);
		lp.safeTransfer(msg.sender, lp.balanceOf(address(this)));
	}

	/**
	 * @notice Gets needed WETH for adding LP
	 * @param lpAmount LP amount
	 * @return wethAmount WETH amount
	 */
	function quoteWETH(uint256 lpAmount) public view returns (uint256 wethAmount) {
		IUniswapV2Pair lpToken = IUniswapV2Pair(lpTokenAddr);

		(uint256 reserve0, uint256 reserve1, ) = lpToken.getReserves();
		uint256 weth = lpToken.token0() != address(prntAddr) ? reserve0 : reserve1;
		uint256 prnt = lpToken.token0() == address(prntAddr) ? reserve0 : reserve1;
		uint256 lpTokenSupply = lpToken.totalSupply();

		uint256 neededPrnt = (prnt * lpAmount) / lpTokenSupply;
		uint256 neededWeth = (prnt * lpAmount) / lpTokenSupply;

		uint256 neededPrntInWeth = router.getAmountIn(neededPrnt, weth, prnt);
		return neededWeth + neededPrntInWeth;
	}

	/**
	 * @notice Zap WETH into LP
	 * @param amount of WETH
	 * @return liquidity LP token amount
	 */
	function zapWETH(uint256 amount) public returns (uint256 liquidity) {
		if (msg.sender != lockZap) revert InsufficientPermission();
		IWETH weth = IWETH(wethAddr);
		weth.transferFrom(msg.sender, address(liquidityZap), amount);
		liquidityZap.addLiquidityWETHOnly(amount, payable(address(this)));
		IERC20 lp = IERC20(lpTokenAddr);

		liquidity = lp.balanceOf(address(this));
		lp.safeTransfer(msg.sender, liquidity);
		_refundDust(prntAddr, wethAddr, msg.sender);
	}

	/**
	 * @notice Returns reserve information.
	 * @return prnt PRNT amount
	 * @return weth WETH amount
	 * @return lpTokenSupply LP token supply
	 */
	function getReserves() public view returns (uint256 prnt, uint256 weth, uint256 lpTokenSupply) {
		IUniswapV2Pair lpToken = IUniswapV2Pair(lpTokenAddr);

		(uint256 reserve0, uint256 reserve1, ) = lpToken.getReserves();
		weth = lpToken.token0() != address(prntAddr) ? reserve0 : reserve1;
		prnt = lpToken.token0() == address(prntAddr) ? reserve0 : reserve1;

		lpTokenSupply = lpToken.totalSupply();
	}

	// UniV2 / SLP LP Token Price
	// Alpha Homora Fair LP Pricing Method (flash loan resistant)
	// https://cmichel.io/pricing-lp-tokens/
	// https://blog.alphafinance.io/fair-lp-token-pricing/
	// https://github.com/AlphaFinanceLab/alpha-homora-v2-contract/blob/master/contracts/oracle/UniswapV2Oracle.sol
	/**
	 * @notice Returns LP price
	 * @param prntPriceInEth price of PRNT in ETH
	 * @return priceInEth LP price in ETH
	 */
	function getLpPrice(uint256 prntPriceInEth) public view returns (uint256 priceInEth) {
		(uint256 prntReserve, uint256 wethReserve, uint256 lpSupply) = getReserves();

		uint256 sqrtK = HomoraMath.sqrt(prntReserve * wethReserve).fdiv(lpSupply); // in 2**112

		// prnt in eth, decis 8
		uint256 px0 = prntPriceInEth * (2 ** 112); // in 2**112
		// eth in eth, decis 8
		uint256 px1 = uint256(100_000_000) * (2 ** 112); // in 2**112

		// fair token0 amt: sqrtK * sqrt(px1/px0)
		// fair token1 amt: sqrtK * sqrt(px0/px1)
		// fair lp price = 2 * sqrt(px0 * px1)
		// split into 2 sqrts multiplication to prevent uint256 overflow (note the 2**112)
		uint256 result = (((sqrtK * 2 * (HomoraMath.sqrt(px0))) / (2 ** 56)) * (HomoraMath.sqrt(px1))) / (2 ** 56);
		priceInEth = result / (2 ** 112);
	}

	/**
	 * @notice Zap WETH and PRNT into LP
	 * @param _wethAmt amount of WETH
	 * @param _prntAmt amount of PRNT
	 * @return liquidity LP token amount
	 */
	function zapTokens(uint256 _wethAmt, uint256 _prntAmt) public returns (uint256 liquidity) {
		if (msg.sender != lockZap) revert InsufficientPermission();
		IWETH weth = IWETH(wethAddr);
		weth.transferFrom(msg.sender, address(this), _wethAmt);
		IERC20(prntAddr).safeTransferFrom(msg.sender, address(this), _prntAmt);
		liquidityZap.standardAdd(_prntAmt, _wethAmt, address(this));
		IERC20 lp = IERC20(lpTokenAddr);
		liquidity = lp.balanceOf(address(this));
		lp.safeTransfer(msg.sender, liquidity);
		_refundDust(prntAddr, wethAddr, msg.sender);
	}

	/**
	 * @notice Returns `quote` of PRNT in WETH
	 * @param tokenAmount amount of PRNT
	 * @return optimalWETHAmount WETH amount
	 */
	function quoteFromToken(uint256 tokenAmount) public view returns (uint256 optimalWETHAmount) {
		optimalWETHAmount = liquidityZap.quoteFromToken(tokenAmount);
	}

	/**
	 * @notice Returns LiquidityZap address
	 */
	function getLiquidityZap() public view returns (address) {
		return address(liquidityZap);
	}

	/**
	 * @notice Sets new LiquidityZap address
	 * @param _liquidityZap LiquidityZap address
	 */
	function setLiquidityZap(address _liquidityZap) external onlyOwner {
		if (_liquidityZap == address(0)) revert AddressZero();
		liquidityZap = ILiquidityZap(_liquidityZap);
		emit LiquidityZapUpdated(_liquidityZap);
	}

	/**
	 * @notice Sets new LockZap address
	 * @param _lockZap LockZap address
	 */
	function setLockZap(address _lockZap) external onlyOwner {
		if (_lockZap == address(0)) revert AddressZero();
		lockZap = _lockZap;
		emit LockZapUpdated(_lockZap);
	}

	/**
	 * @notice Returns PRNT price in ETH
	 * @return priceInEth price of PRNT
	 */
	function getPrice() public view returns (uint256 priceInEth) {
		(uint256 prnt, uint256 weth, ) = getReserves();
		if (prnt > 0) {
			priceInEth = (weth * (10 ** 8)) / prnt;
		}
	}

	/**
	 * @notice Calculate quote in WETH from token
	 * @param _inToken input token
	 * @param _wethAmount WETH amount
	 * @return tokenAmount token amount
	 */
	function quoteSwap(address _inToken, uint256 _wethAmount) public view returns (uint256 tokenAmount) {
		address[] memory path = new address[](2);
		path[0] = _inToken;
		path[1] = wethAddr;
		uint256[] memory amountsIn = router.getAmountsIn(_wethAmount, path);
		return amountsIn[0];
	}

	/**
	 * @dev Helper function to swap a token to weth given an {_inToken} and swap {_amount}.
	 * Will revert if the output is under the {_minAmountOut}
	 * @param _inToken Input token for swap
	 * @param _amount Amount of input tokens
	 * @param _minAmountOut Minimum output amount
	 */
	function swapToWeth(address _inToken, uint256 _amount, uint256 _minAmountOut) external {
		if (msg.sender != lockZap) revert InsufficientPermission();
		address[] memory path = new address[](2);
		path[0] = _inToken;
		path[1] = wethAddr;
		IERC20(_inToken).forceApprove(address(router), _amount);
		router.swapExactTokensForTokens(_amount, _minAmountOut, path, msg.sender, block.timestamp);
	}
}

contracts/test/TestUniswapPoolHelper.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.12;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../prime/zap/helpers/UniswapPoolHelper.sol";

contract TestUniswapPoolHelper is UniswapPoolHelper {
	using SafeERC20 for IERC20;

	function swap(uint256 _amount, address, address, address) public returns (uint256 amountOut) {
		IUniswapV2Pair lpToken = IUniswapV2Pair(lpTokenAddr);
		(uint256 reserve0, uint256 reserve1, ) = lpToken.getReserves();

		(address token0, address token1) = UniswapV2Library.sortTokens(address(wethAddr), prntAddr);

		uint256 reserveWeth = token0 == address(wethAddr) ? reserve0 : reserve1;
		uint256 reserveTokens = token0 == address(wethAddr) ? reserve1 : reserve0;

		uint256 outETH = UniswapV2Library.getAmountOut(_amount, reserveTokens, reserveWeth);

		IERC20(prntAddr).safeTransfer(lpTokenAddr, _amount);

		IUniswapV2Pair(lpTokenAddr).swap(
			address(wethAddr) == token0 ? outETH : 0,
			address(wethAddr) == token1 ? outETH : 0,
			address(this),
			""
		);

		amountOut = IERC20(address(wethAddr)).balanceOf(address(this));
	}

	function sell(uint256 _amount) public returns (uint256 amountOut) {
		return
			swap(
				_amount,
				0x0000000000000000000000000000000000000000,
				0x0000000000000000000000000000000000000000,
				0x0000000000000000000000000000000000000000
			);
	}
}

