// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import {DustRefunder} from "./DustRefunder.sol";
import {UniswapV2Library} from "../../../uniswap-solc-0.8/libraries/UniswapV2Library.sol";
import {IUniswapV2Pair} from "../../../uniswap-solc-0.8/interfaces/IUniswapV2Pair.sol";
import {IUniswapV2Factory} from "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {HomoraMath} from "../../../dependencies/math/HomoraMath.sol";
import {IUniswapV2Router02} from "../../../interfaces/uniswap/IUniswapV2Router02.sol";
import {ILiquidityZap} from "../../../interfaces/ILiquidityZap.sol";
import {IWETH} from "../../../interfaces/IWETH.sol";

/// @title Uniswap Pool Helper Contract
/// @author Prime
contract UniswapPoolHelper is Initializable, OwnableUpgradeable, DustRefunder {
	using SafeERC20 for IERC20;
	using HomoraMath for uint256;

	/********************** Events ***********************/
	event LiquidityZapUpdated(address indexed _liquidityZap);

	event LockZapUpdated(address indexed _lockZap);

	/********************** Errors ***********************/
	error AddressZero();
	error InsufficientPermission();

	address public lpTokenAddr;
	address public prntAddr;
	address public wethAddr;

	IUniswapV2Router02 public router;
	ILiquidityZap public liquidityZap;
	address public lockZap;

	constructor() {
		_disableInitializers();
	}

	/**
	 * @notice Initializer
	 * @param _prntAddr PRNT address
	 * @param _wethAddr WETH address
	 * @param _routerAddr Uniswap router address
	 * @param _liquidityZap LiquidityZap addrress
	 */
	function initialize(
		address _prntAddr,
		address _wethAddr,
		address _routerAddr,
		ILiquidityZap _liquidityZap
	) external initializer {
		if (_prntAddr == address(0)) revert AddressZero();
		if (_wethAddr == address(0)) revert AddressZero();
		if (_routerAddr == address(0)) revert AddressZero();
		if (address(_liquidityZap) == address(0)) revert AddressZero();

		__Ownable_init();

		prntAddr = _prntAddr;
		wethAddr = _wethAddr;

		router = IUniswapV2Router02(_routerAddr);
		liquidityZap = _liquidityZap;
	}

	/**
	 * @notice Initialize PRNT/WETH pool and liquidity zap
	 */
	function initializePool() public onlyOwner {
		lpTokenAddr = IUniswapV2Factory(router.factory()).createPair(prntAddr, wethAddr);

		IERC20 prnt = IERC20(prntAddr);
		prnt.forceApprove(address(router), type(uint256).max);
		prnt.forceApprove(address(liquidityZap), type(uint256).max);
		IERC20(wethAddr).approve(address(liquidityZap), type(uint256).max);
		IERC20(wethAddr).approve(address(router), type(uint256).max);

		router.addLiquidity(
			address(prnt),
			wethAddr,
			prnt.balanceOf(address(this)),
			IERC20(wethAddr).balanceOf(address(this)),
			0,
			0,
			address(this),
			block.timestamp
		);

		IERC20 lp = IERC20(lpTokenAddr);
		lp.safeTransfer(msg.sender, lp.balanceOf(address(this)));
	}

	/**
	 * @notice Gets needed WETH for adding LP
	 * @param lpAmount LP amount
	 * @return wethAmount WETH amount
	 */
	function quoteWETH(uint256 lpAmount) public view returns (uint256 wethAmount) {
		IUniswapV2Pair lpToken = IUniswapV2Pair(lpTokenAddr);

		(uint256 reserve0, uint256 reserve1, ) = lpToken.getReserves();
		uint256 weth = lpToken.token0() != address(prntAddr) ? reserve0 : reserve1;
		uint256 prnt = lpToken.token0() == address(prntAddr) ? reserve0 : reserve1;
		uint256 lpTokenSupply = lpToken.totalSupply();

		uint256 neededPrnt = (prnt * lpAmount) / lpTokenSupply;
		uint256 neededWeth = (prnt * lpAmount) / lpTokenSupply;

		uint256 neededPrntInWeth = router.getAmountIn(neededPrnt, weth, prnt);
		return neededWeth + neededPrntInWeth;
	}

	/**
	 * @notice Zap WETH into LP
	 * @param amount of WETH
	 * @return liquidity LP token amount
	 */
	function zapWETH(uint256 amount) public returns (uint256 liquidity) {
		if (msg.sender != lockZap) revert InsufficientPermission();
		IWETH weth = IWETH(wethAddr);
		weth.transferFrom(msg.sender, address(liquidityZap), amount);
		liquidityZap.addLiquidityWETHOnly(amount, payable(address(this)));
		IERC20 lp = IERC20(lpTokenAddr);

		liquidity = lp.balanceOf(address(this));
		lp.safeTransfer(msg.sender, liquidity);
		_refundDust(prntAddr, wethAddr, msg.sender);
	}

	/**
	 * @notice Returns reserve information.
	 * @return prnt PRNT amount
	 * @return weth WETH amount
	 * @return lpTokenSupply LP token supply
	 */
	function getReserves() public view returns (uint256 prnt, uint256 weth, uint256 lpTokenSupply) {
		IUniswapV2Pair lpToken = IUniswapV2Pair(lpTokenAddr);

		(uint256 reserve0, uint256 reserve1, ) = lpToken.getReserves();
		weth = lpToken.token0() != address(prntAddr) ? reserve0 : reserve1;
		prnt = lpToken.token0() == address(prntAddr) ? reserve0 : reserve1;

		lpTokenSupply = lpToken.totalSupply();
	}

	// UniV2 / SLP LP Token Price
	// Alpha Homora Fair LP Pricing Method (flash loan resistant)
	// https://cmichel.io/pricing-lp-tokens/
	// https://blog.alphafinance.io/fair-lp-token-pricing/
	// https://github.com/AlphaFinanceLab/alpha-homora-v2-contract/blob/master/contracts/oracle/UniswapV2Oracle.sol
	/**
	 * @notice Returns LP price
	 * @param prntPriceInEth price of PRNT in ETH
	 * @return priceInEth LP price in ETH
	 */
	function getLpPrice(uint256 prntPriceInEth) public view returns (uint256 priceInEth) {
		(uint256 prntReserve, uint256 wethReserve, uint256 lpSupply) = getReserves();

		uint256 sqrtK = HomoraMath.sqrt(prntReserve * wethReserve).fdiv(lpSupply); // in 2**112

		// prnt in eth, decis 8
		uint256 px0 = prntPriceInEth * (2 ** 112); // in 2**112
		// eth in eth, decis 8
		uint256 px1 = uint256(100_000_000) * (2 ** 112); // in 2**112

		// fair token0 amt: sqrtK * sqrt(px1/px0)
		// fair token1 amt: sqrtK * sqrt(px0/px1)
		// fair lp price = 2 * sqrt(px0 * px1)
		// split into 2 sqrts multiplication to prevent uint256 overflow (note the 2**112)
		uint256 result = (((sqrtK * 2 * (HomoraMath.sqrt(px0))) / (2 ** 56)) * (HomoraMath.sqrt(px1))) / (2 ** 56);
		priceInEth = result / (2 ** 112);
	}

	/**
	 * @notice Zap WETH and RDNt into LP
	 * @param _wethAmt amount of WETH
	 * @param _prntAmt amount of PRNT
	 * @return liquidity LP token amount
	 */
	function zapTokens(uint256 _wethAmt, uint256 _prntAmt) public returns (uint256 liquidity) {
		if (msg.sender != lockZap) revert InsufficientPermission();
		IWETH weth = IWETH(wethAddr);
		weth.transferFrom(msg.sender, address(this), _wethAmt);
		IERC20(prntAddr).safeTransferFrom(msg.sender, address(this), _prntAmt);
		liquidityZap.standardAdd(_prntAmt, _wethAmt, address(this));
		IERC20 lp = IERC20(lpTokenAddr);
		liquidity = lp.balanceOf(address(this));
		lp.safeTransfer(msg.sender, liquidity);
		_refundDust(prntAddr, wethAddr, msg.sender);
	}

	/**
	 * @notice Returns `quote` of PRNT in WETH
	 * @param tokenAmount amount of PRNT
	 * @return optimalWETHAmount WETH amount
	 */
	function quoteFromToken(uint256 tokenAmount) public view returns (uint256 optimalWETHAmount) {
		optimalWETHAmount = liquidityZap.quoteFromToken(tokenAmount);
	}

	/**
	 * @notice Returns LiquidityZap address
	 */
	function getLiquidityZap() public view returns (address) {
		return address(liquidityZap);
	}

	/**
	 * @notice Sets new LiquidityZap address
	 * @param _liquidityZap LiquidityZap address
	 */
	function setLiquidityZap(address _liquidityZap) external onlyOwner {
		if (_liquidityZap == address(0)) revert AddressZero();
		liquidityZap = ILiquidityZap(_liquidityZap);
		emit LiquidityZapUpdated(_liquidityZap);
	}

	/**
	 * @notice Sets new LockZap address
	 * @param _lockZap LockZap address
	 */
	function setLockZap(address _lockZap) external onlyOwner {
		if (_lockZap == address(0)) revert AddressZero();
		lockZap = _lockZap;
		emit LockZapUpdated(_lockZap);
	}

	/**
	 * @notice Returns PRNT price in ETH
	 * @return priceInEth price of PRNT
	 */
	function getPrice() public view returns (uint256 priceInEth) {
		(uint256 prnt, uint256 weth, ) = getReserves();
		if (prnt > 0) {
			priceInEth = (weth * (10 ** 8)) / prnt;
		}
	}

	/**
	 * @notice Calculate quote in WETH from token
	 * @param _inToken input token
	 * @param _wethAmount WETH amount
	 * @return tokenAmount token amount
	 */
	function quoteSwap(address _inToken, uint256 _wethAmount) public view returns (uint256 tokenAmount) {
		address[] memory path = new address[](2);
		path[0] = _inToken;
		path[1] = wethAddr;
		uint256[] memory amountsIn = router.getAmountsIn(_wethAmount, path);
		return amountsIn[0];
	}

	/**
	 * @dev Helper function to swap a token to weth given an {_inToken} and swap {_amount}.
	 * Will revert if the output is under the {_minAmountOut}
	 * @param _inToken Input token for swap
	 * @param _amount Amount of input tokens
	 * @param _minAmountOut Minimum output amount
	 */
	function swapToWeth(address _inToken, uint256 _amount, uint256 _minAmountOut) external {
		if (msg.sender != lockZap) revert InsufficientPermission();
		address[] memory path = new address[](2);
		path[0] = _inToken;
		path[1] = wethAddr;
		IERC20(_inToken).forceApprove(address(router), _amount);
		router.swapExactTokensForTokens(_amount, _minAmountOut, path, msg.sender, block.timestamp);
	}
}

contracts/prime/zap/LockZap.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.12;

import {DustRefunder} from "./helpers/DustRefunder.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {PausableUpgradeable} from "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import {IMultiFeeDistribution} from "../../interfaces/IMultiFeeDistribution.sol";
import {ILendingPool, DataTypes} from "../../interfaces/ILendingPool.sol";
import {IPoolHelper} from "../../interfaces/IPoolHelper.sol";
import {IPriceProvider} from "../../interfaces/IPriceProvider.sol";
import {IAaveOracle} from "../../interfaces/IAaveOracle.sol";
import {IChainlinkAggregator} from "../../interfaces/IChainlinkAggregator.sol";
import {IWETH} from "../../interfaces/IWETH.sol";
import {IPriceOracle} from "../../interfaces/IPriceOracle.sol";
import {TransferHelper} from "../libraries/TransferHelper.sol";
import {UniV2Helper} from "../libraries/UniV2Helper.sol";

/// @title LockZap contract
/// @author Prime
contract LockZap is Initializable, OwnableUpgradeable, PausableUpgradeable, DustRefunder {
	using SafeERC20 for IERC20;

	/// @notice The maximum amount of slippage that a user can set for the execution of Zaps
	/// @dev If the slippage limit of the LockZap contract is lower then that of the Compounder, transactions might fail unexpectedly.
	///      Therefore ensure that this slippage limit is equal to that of the Compounder contract.
	uint256 public constant MAX_SLIPPAGE = 8500; //15%

	/// @notice RATIO Divisor
	uint256 public constant RATIO_DIVISOR = 10000;

	/// @notice Base Percent
	uint256 public constant BASE_PERCENT = 100;

	/// @notice Borrow rate mode
	uint256 public constant VARIABLE_INTEREST_RATE_MODE = 2;

	/// @notice We don't utilize any specific referral code for borrows perfomed via zaps
	uint16 public constant REFERRAL_CODE = 0;

	/// @notice Wrapped ETH
	IWETH public weth;

	/// @notice PRNT token address
	address public prntAddr;

	/// @notice Multi Fee distribution contract
	IMultiFeeDistribution public mfd;

	/// @notice Lending Pool contract
	ILendingPool public lendingPool;

	/// @notice Pool helper contract used for PRNT-WETH swaps
	IPoolHelper public poolHelper;

	/// @notice Price provider contract
	IPriceProvider public priceProvider;

	/// @notice aave oracle contract
	IAaveOracle public aaveOracle;

	/// @notice parameter to set the ratio of ETH in the LP token, can be 2000 for an 80/20 bal lp
	uint256 public ethLPRatio;

	/// @notice AMM router used for all non PRNT-WETH swaps on Arbitrum
	address public uniRouter;

	/********************** Events ***********************/
	/// @notice Emitted when zap is done
	event Zapped(
		bool _borrow,
		uint256 _ethAmt,
		uint256 _prntAmt,
		address indexed _from,
		address indexed _onBehalf,
		uint256 _lockTypeIndex
	);

	event PriceProviderUpdated(address indexed _provider);

	event MfdUpdated(address indexed _mfdAddr);

	event PoolHelperUpdated(address indexed _poolHelper);

	event UniRouterUpdated(address indexed _uniRouter);

	/********************** Errors ***********************/
	error AddressZero();

	error InvalidRatio();

	error InvalidLockLength();

	error AmountZero();

	error SlippageTooHigh();

	error SpecifiedSlippageExceedLimit();

	error InvalidZapETHSource();

	error ReceivedETHOnAlternativeAssetZap();

	error InsufficientETH();

	error EthTransferFailed();

	constructor() {
		_disableInitializers();
	}

	/**
	 * @notice Initializer
	 * @param _rndtPoolHelper Pool helper address used for PRNT-WETH swaps
	 * @param _uniRouter UniV2 router address used for all non PRNT-WETH swaps
	 * @param _lendingPool Lending pool
	 * @param _weth weth address
	 * @param _prntAddr PRNT token address
	 * @param _ethLPRatio ratio of ETH in the LP token, can be 2000 for an 80/20 bal lp
	 * @param _aaveOracle Aave oracle address
	 */
	function initialize(
		IPoolHelper _rndtPoolHelper,
		address _uniRouter,
		ILendingPool _lendingPool,
		IWETH _weth,
		address _prntAddr,
		uint256 _ethLPRatio,
		IAaveOracle _aaveOracle
	) external initializer {
		if (address(_rndtPoolHelper) == address(0)) revert AddressZero();
		if (address(_uniRouter) == address(0)) revert AddressZero();
		if (address(_lendingPool) == address(0)) revert AddressZero();
		if (address(_weth) == address(0)) revert AddressZero();
		if (_prntAddr == address(0)) revert AddressZero();
		if (_ethLPRatio == 0 || _ethLPRatio >= RATIO_DIVISOR) revert InvalidRatio();
		if (address(_aaveOracle) == address(0)) revert AddressZero();

		__Ownable_init();
		__Pausable_init();

		lendingPool = _lendingPool;
		poolHelper = _rndtPoolHelper;
		uniRouter = _uniRouter;
		weth = _weth;
		prntAddr = _prntAddr;
		ethLPRatio = _ethLPRatio;
		aaveOracle = _aaveOracle;
	}

	receive() external payable {}

	/**
	 * @notice Set Price Provider.
	 * @param _provider Price provider contract address.
	 */
	function setPriceProvider(address _provider) external onlyOwner {
		if (_provider == address(0)) revert AddressZero();
		priceProvider = IPriceProvider(_provider);
		emit PriceProviderUpdated(_provider);
	}

	/**
	 * @notice Set AAVE Oracle used to fetch asset prices in USD.
	 * @param _aaveOracle oracle contract address.
	 */
	function setAaveOracle(address _aaveOracle) external onlyOwner {
		if (_aaveOracle == address(0)) revert AddressZero();
		aaveOracle = IAaveOracle(_aaveOracle);
	}

	/**
	 * @notice Set Multi fee distribution contract.
	 * @param _mfdAddr New contract address.
	 */
	function setMfd(address _mfdAddr) external onlyOwner {
		if (_mfdAddr == address(0)) revert AddressZero();
		mfd = IMultiFeeDistribution(_mfdAddr);
		emit MfdUpdated(_mfdAddr);
	}

	/**
	 * @notice Set Pool Helper contract used fror WETH-PRNT swaps
	 * @param _poolHelper New PoolHelper contract address.
	 */
	function setPoolHelper(address _poolHelper) external onlyOwner {
		if (_poolHelper == address(0)) revert AddressZero();
		poolHelper = IPoolHelper(_poolHelper);
		emit PoolHelperUpdated(_poolHelper);
	}

	/**
	 * @notice Set Univ2 style router contract address used for all non PRNT<>WETH swaps
	 * @param _uniRouter New PoolHelper contract address.
	 */
	function setUniRouter(address _uniRouter) external onlyOwner {
		if (_uniRouter == address(0)) revert AddressZero();
		uniRouter = _uniRouter;
		emit UniRouterUpdated(_uniRouter);
	}

	/**
	 * @notice Returns pool helper address used for PRNT-WETH swaps
	 */
	function getPoolHelper() external view returns (address) {
		return address(poolHelper);
	}

	/**
	 * @notice Returns uni router address used for all non PRNT-WETH swaps
	 */
	function getUniRouter() external view returns (address) {
		return address(uniRouter);
	}

	/**
	 * @notice Get Variable debt token address
	 * @param _asset underlying.
	 */
	function getVDebtToken(address _asset) external view returns (address) {
		DataTypes.ReserveData memory reserveData = lendingPool.getReserveData(_asset);
		return reserveData.variableDebtTokenAddress;
	}

	/**
	 * @notice Calculate amount of specified tokens received for selling PRNT
	 * @dev this function is mainly used to calculate how much of the specified token is needed to match the provided PRNT amount when providing liquidity to an AMM
	 * @param _token address of the token that would be received
	 * @param _amount of PRNT to be sold
	 * @return amount of _token received
	 */
	function quoteFromToken(address _token, uint256 _amount) public view returns (uint256) {
		address weth_ = address(weth);
		if (_token != weth_) {
			uint256 wethAmount = poolHelper.quoteFromToken(_amount);
			return UniV2Helper._quoteSwap(uniRouter, weth_, _token, wethAmount);
		}
		return poolHelper.quoteFromToken(_amount);
	}

	/**
	 * @notice Zap tokens to stake LP
	 * @param _borrow option to borrow ETH
	 * @param _asset to be used for zapping
	 * @param _assetAmt amount of weth.
	 * @param _prntAmt amount of PRNT.
	 * @param _lockTypeIndex lock length index.
	 * @param _slippage maximum amount of slippage allowed for any occurring trades
	 * @return LP amount
	 */
	function zap(
		bool _borrow,
		address _asset,
		uint256 _assetAmt,
		uint256 _prntAmt,
		uint256 _lockTypeIndex,
		uint256 _slippage
	) public payable whenNotPaused returns (uint256) {
		return
			_zap(_borrow, _asset, _assetAmt, _prntAmt, msg.sender, msg.sender, _lockTypeIndex, msg.sender, _slippage);
	}

	/**
	 * @notice Zap tokens to stake LP
	 * @dev It will use default lock index
	 * @param _borrow option to borrow ETH
	 * @param _asset to be used for zapping
	 * @param _assetAmt amount of weth.
	 * @param _prntAmt amount of PRNT.
	 * @param _onBehalf user address to be zapped.
	 * @param _slippage maximum amount of slippage allowed for any occurring trades
	 * @return LP amount
	 */
	function zapOnBehalf(
		bool _borrow,
		address _asset,
		uint256 _assetAmt,
		uint256 _prntAmt,
		address _onBehalf,
		uint256 _slippage
	) public payable whenNotPaused returns (uint256) {
		uint256 duration = mfd.defaultLockIndex(_onBehalf);
		return _zap(_borrow, _asset, _assetAmt, _prntAmt, msg.sender, _onBehalf, duration, _onBehalf, _slippage);
	}

	/**
	 * @notice Zap tokens from vesting
	 * @param _borrow option to borrow ETH
	 * @param _asset to be used for zapping
	 * @param _assetAmt amount of _asset tokens used to create dLP position
	 * @param _lockTypeIndex lock length index. cannot be shortest option (index 0)
	 * @param _slippage maximum amount of slippage allowed for any occurring trades
	 * @return LP amount
	 */
	function zapFromVesting(
		bool _borrow,
		address _asset,
		uint256 _assetAmt,
		uint256 _lockTypeIndex,
		uint256 _slippage
	) public payable whenNotPaused returns (uint256) {
		if (_lockTypeIndex == 0) revert InvalidLockLength();
		uint256 prntAmt = mfd.zapVestingToLp(msg.sender);

		return
			_zap(_borrow, _asset, _assetAmt, prntAmt, address(this), msg.sender, _lockTypeIndex, msg.sender, _slippage);
	}

	/**
	 * @notice Calculates slippage ratio from usd value to LP
	 * @param _assetValueUsd amount value in USD used to create LP pair
	 * @param _liquidity LP token amount
	 */
	function _calcSlippage(uint256 _assetValueUsd, uint256 _liquidity) internal returns (uint256 ratio) {
		priceProvider.update();
		uint256 lpAmountValueUsd = (_liquidity * priceProvider.getLpTokenPriceUsd()) / 1e18;
		ratio = (lpAmountValueUsd * (RATIO_DIVISOR)) / (_assetValueUsd);
	}

	/**
	 * @notice Zap into LP
	 * @param _borrow option to borrow ETH
	 * @param _asset that will be used to zap.
	 * @param _assetAmt amount of assets to be zapped
	 * @param _prntAmt amount of PRNT.
	 * @param _from src address of PRNT
	 * @param _onBehalf of the user.
	 * @param _lockTypeIndex lock length index.
	 * @param _refundAddress dust is refunded to this address.
	 * @param _slippage maximum amount of slippage allowed for any occurring trades
	 * @return liquidity LP amount
	 */
	function _zap(
		bool _borrow,
		address _asset,
		uint256 _assetAmt,
		uint256 _prntAmt,
		address _from,
		address _onBehalf,
		uint256 _lockTypeIndex,
		address _refundAddress,
		uint256 _slippage
	) internal returns (uint256 liquidity) {
		IWETH weth_ = weth;
		if (_asset == address(0)) {
			_asset = address(weth_);
		}
		if (_slippage == 0) {
			_slippage = MAX_SLIPPAGE;
		} else {
			if (MAX_SLIPPAGE > _slippage || _slippage > RATIO_DIVISOR) revert SpecifiedSlippageExceedLimit();
		}
		bool isAssetWeth = _asset == address(weth_);

		// Handle pure ETH
		if (msg.value > 0) {
			if (!isAssetWeth) revert ReceivedETHOnAlternativeAssetZap();
			if (_borrow) revert InvalidZapETHSource();
			_assetAmt = msg.value;
			weth_.deposit{value: _assetAmt}();
		}
		if (_assetAmt == 0) revert AmountZero();
		uint256 assetAmountValueUsd = (_assetAmt * aaveOracle.getAssetPrice(_asset)) /
			(10 ** IERC20Metadata(_asset).decimals());

		// Handle borrowing logic
		if (_borrow) {
			// Borrow the asset on the users behalf
			lendingPool.borrow(_asset, _assetAmt, VARIABLE_INTEREST_RATE_MODE, REFERRAL_CODE, msg.sender);

			// If asset isn't WETH, swap for WETH
			if (!isAssetWeth) {
				_assetAmt = UniV2Helper._swap(uniRouter, _asset, address(weth_), _assetAmt);
			}
		} else if (msg.value == 0) {
			// Transfer asset from user
			IERC20(_asset).transferFrom(msg.sender, address(this), _assetAmt);
			if (!isAssetWeth) {
				_assetAmt = UniV2Helper._swap(uniRouter, _asset, address(weth_), _assetAmt);
			}
		}

		weth_.approve(address(poolHelper), _assetAmt);
		//case where prnt is matched with provided ETH
		if (_prntAmt != 0) {
			// _from == this when zapping from vesting
			if (_from != address(this)) {
				IERC20(prntAddr).safeTransferFrom(msg.sender, address(this), _prntAmt);
			}

			IERC20(prntAddr).forceApprove(address(poolHelper), _prntAmt);
			liquidity = poolHelper.zapTokens(_assetAmt, _prntAmt);
			assetAmountValueUsd = (assetAmountValueUsd * RATIO_DIVISOR) / ethLPRatio;
		} else {
			liquidity = poolHelper.zapWETH(_assetAmt);
		}

		if (address(priceProvider) != address(0)) {
			if (_calcSlippage(assetAmountValueUsd, liquidity) < _slippage) revert SlippageTooHigh();
		}

		IERC20(poolHelper.lpTokenAddr()).forceApprove(address(mfd), liquidity);
		mfd.stake(liquidity, _onBehalf, _lockTypeIndex);
		emit Zapped(_borrow, _assetAmt, _prntAmt, _from, _onBehalf, _lockTypeIndex);

		_refundDust(prntAddr, _asset, _refundAddress);
	}

	/**
	 * @notice Pause zapping operation.
	 */
	function pause() external onlyOwner {
		_pause();
	}

	/**
	 * @notice Unpause zapping operation.
	 */
	function unpause() external onlyOwner {
		_unpause();
	}

	/**
	 * @notice Allows owner to recover ETH locked in this contract.
	 * @param to ETH receiver
	 * @param value ETH amount
	 */
	function withdrawLockedETH(address to, uint256 value) external onlyOwner {
		TransferHelper.safeTransferETH(to, value);
	}
}

contracts/test/MockNewMiddleFeeDistribut.content:
// SPDX-License-Identifier: MIT

