// SPDX-License-Identifier: GPL-3.0-or-later

pragma solidity 0.8.12;

// computes square roots using the babylonian method
// https://en.wikipedia.org/wiki/Methods_of_computing_square_roots#Babylonian_method
library Babylonian {
	// credit for this implementation goes to
	// https://github.com/abdk-consulting/abdk-libraries-solidity/blob/master/ABDKMath64x64.sol#L687
	function sqrt(uint256 x) internal pure returns (uint256) {
		if (x == 0) return 0;
		// this block is equivalent to r = uint256(1) << (BitMath.mostSignificantBit(x) / 2);
		// however that code costs significantly more gas
		uint256 xx = x;
		uint256 r = 1;
		if (xx >= 0x100000000000000000000000000000000) {
			xx >>= 128;
			r <<= 64;
		}
		if (xx >= 0x10000000000000000) {
			xx >>= 64;
			r <<= 32;
		}
		if (xx >= 0x100000000) {
			xx >>= 32;
			r <<= 16;
		}
		if (xx >= 0x10000) {
			xx >>= 16;
			r <<= 8;
		}
		if (xx >= 0x100) {
			xx >>= 8;
			r <<= 4;
		}
		if (xx >= 0x10) {
			xx >>= 4;
			r <<= 2;
		}
		if (xx >= 0x8) {
			r <<= 1;
		}
		r = (r + x / r) >> 1;
		r = (r + x / r) >> 1;
		r = (r + x / r) >> 1;
		r = (r + x / r) >> 1;
		r = (r + x / r) >> 1;
		r = (r + x / r) >> 1;
		r = (r + x / r) >> 1; // Seven iterations should be enough
		uint256 r1 = x / r;
		return (r < r1 ? r : r1);
	}
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.12;

/// @title Contains 512-bit math functions
/// @notice Facilitates multiplication and division that can have overflow of an intermediate value without any loss of precision
/// @dev Handles "phantom overflow" i.e., allows multiplication and division where an intermediate value overflows 256 bits
library FullMath {
	/// @notice Calculates floor(abdenominator) with full precision. Throws if result overflows a uint256 or denominator == 0
	/// @param a The multiplicand
	/// @param b The multiplier
	/// @param denominator The divisor
	/// @return result The 256-bit result
	/// @dev Credit to Remco Bloemen under MIT license https://xn--2-umb.com/21/muldiv
	function mulDiv(uint256 a, uint256 b, uint256 denominator) internal pure returns (uint256 result) {
		unchecked {
			// 512-bit multiply [prod1 prod0] = a * b
			// Compute the product mod 2**256 and mod 2**256 - 1
			// then use the Chinese Remainder Theorem to reconstruct
			// the 512 bit result. The result is stored in two 256
			// variables such that product = prod1 * 2**256 + prod0
			uint256 prod0; // Least significant 256 bits of the product
			uint256 prod1; // Most significant 256 bits of the product
			assembly {
				let mm := mulmod(a, b, not(0))
				prod0 := mul(a, b)
				prod1 := sub(sub(mm, prod0), lt(mm, prod0))
			}

			// Handle non-overflow cases, 256 by 256 division
			if (prod1 == 0) {
				require(denominator > 0);
				assembly {
					result := div(prod0, denominator)
				}
				return result;
			}

			// Make sure the result is less than 2**256.
			// Also prevents denominator == 0
			require(denominator > prod1);

			///////////////////////////////////////////////
			// 512 by 256 division.
			///////////////////////////////////////////////

			// Make division exact by subtracting the remainder from [prod1 prod0]
			// Compute remainder using mulmod
			uint256 remainder;
			assembly {
				remainder := mulmod(a, b, denominator)
			}
			// Subtract 256 bit number from 512 bit number
			assembly {
				prod1 := sub(prod1, gt(remainder, prod0))
				prod0 := sub(prod0, remainder)
			}

			// Factor powers of two out of denominator
			// Compute largest power of two divisor of denominator.
			// Always >= 1.
			uint256 twos = (0 - denominator) & denominator;
			// Divide denominator by power of two
			assembly {
				denominator := div(denominator, twos)
			}

			// Divide [prod1 prod0] by the factors of two
			assembly {
				prod0 := div(prod0, twos)
			}
			// Shift in bits from prod1 into prod0. For this we need
			// to flip `twos` such that it is 2**256 / twos.
			// If twos is zero, then it becomes one
			assembly {
				twos := add(div(sub(0, twos), twos), 1)
			}
			prod0 |= prod1 * twos;

			// Invert denominator mod 2**256
			// Now that denominator is an odd number, it has an inverse
			// modulo 2**256 such that denominator * inv = 1 mod 2**256.
			// Compute the inverse by starting with a seed that is correct
			// correct for four bits. That is, denominator * inv = 1 mod 2**4
			uint256 inv = (3 * denominator) ^ 2;
			// Now use Newton-Raphson iteration to improve the precision.
			// Thanks to Hensel's lifting lemma, this also works in modular
			// arithmetic, doubling the correct bits in each step.
			inv *= 2 - denominator * inv; // inverse mod 2**8
			inv *= 2 - denominator * inv; // inverse mod 2**16
			inv *= 2 - denominator * inv; // inverse mod 2**32
			inv *= 2 - denominator * inv; // inverse mod 2**64
			inv *= 2 - denominator * inv; // inverse mod 2**128
			inv *= 2 - denominator * inv; // inverse mod 2**256

			// Because the division is now exact we can divide by multiplying
			// with the modular inverse of denominator. This will give us the
			// correct result modulo 2**256. Since the precoditions guarantee
			// that the outcome is less than 2**256, this is the final result.
			// We don't need to compute the high bits of the result and prod1
			// is no longer required.
			result = prod0 * inv;
			return result;
		}
	}

	/// @notice Calculates ceil(abdenominator) with full precision. Throws if result overflows a uint256 or denominator == 0
	/// @param a The multiplicand
	/// @param b The multiplier
	/// @param denominator The divisor
	/// @return result The 256-bit result
	function mulDivRoundingUp(uint256 a, uint256 b, uint256 denominator) internal pure returns (uint256 result) {
		unchecked {
			result = mulDiv(a, b, denominator);
			if (mulmod(a, b, denominator) > 0) {
				require(result < type(uint256).max);
				result++;
			}
		}
	}
}

// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.12;

// a library for performing overflow-safe math, courtesy of DappHub (https://github.com/dapphub/ds-math)

library SafeMath {
	function add(uint x, uint y) internal pure returns (uint z) {
		require((z = x + y) >= x, "ds-math-add-overflow");
	}

	function sub(uint x, uint y) internal pure returns (uint z) {
		require((z = x - y) <= x, "ds-math-sub-underflow");
	}

	function mul(uint x, uint y) internal pure returns (uint z) {
		require(y == 0 || (z = x * y) / y == x, "ds-math-mul-overflow");
	}
}

// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.12;

import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
// import '@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol';
import "../../core/interfaces/IUniswapV2Factory.sol";
import "./SafeMath.sol";

library UniswapV2Library {
	using SafeMath for uint;

	// returns sorted token addresses, used to handle return values from pairs sorted in this order
	function sortTokens(address tokenA, address tokenB) internal pure returns (address token0, address token1) {
		require(tokenA != tokenB, "UniswapV2Library: IDENTICAL_ADDRESSES");
		(token0, token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
		require(token0 != address(0), "UniswapV2Library: ZERO_ADDRESS");
	}

	// calculates the CREATE2 address for a pair without making any external calls
	function pairFor(address factory, address tokenA, address tokenB) internal view returns (address pair) {
		(address token0, address token1) = sortTokens(tokenA, tokenB);
		pair = address(
			uint160(
				uint(
					keccak256(
						abi.encodePacked(
							hex"ff",
							factory,
							keccak256(abi.encodePacked(token0, token1)),
							IUniswapV2Factory(factory).getInitHash()
							// hex'96e8ac4277198ff8b6f785478aa9a39f403cb768dd02cbee326c3e7da348845f' // init code hash
						)
					)
				)
			)
		);
	}

	// fetches and sorts the reserves for a pair
	function getReserves(
		address factory,
		address tokenA,
		address tokenB
	) internal view returns (uint reserveA, uint reserveB) {
		(address token0, ) = sortTokens(tokenA, tokenB);
		(uint reserve0, uint reserve1, ) = IUniswapV2Pair(pairFor(factory, tokenA, tokenB)).getReserves();

		(reserveA, reserveB) = tokenA == token0 ? (reserve0, reserve1) : (reserve1, reserve0);
	}

	// given some amount of an asset and pair reserves, returns an equivalent amount of the other asset
	function quote(uint amountA, uint reserveA, uint reserveB) internal pure returns (uint amountB) {
		require(amountA > 0, "UniswapV2Library: INSUFFICIENT_AMOUNT");
		require(reserveA > 0 && reserveB > 0, "UniswapV2Library: INSUFFICIENT_LIQUIDITY");
		amountB = amountA.mul(reserveB) / reserveA;
	}

	// given an input amount of an asset and pair reserves, returns the maximum output amount of the other asset
	function getAmountOut(uint amountIn, uint reserveIn, uint reserveOut) internal pure returns (uint amountOut) {
		require(amountIn > 0, "UniswapV2Library: INSUFFICIENT_INPUT_AMOUNT");
		require(reserveIn > 0 && reserveOut > 0, "UniswapV2Library: INSUFFICIENT_LIQUIDITY");
		uint amountInWithFee = amountIn.mul(997);
		uint numerator = amountInWithFee.mul(reserveOut);
		uint denominator = reserveIn.mul(1000).add(amountInWithFee);
		amountOut = numerator / denominator;
	}

	// given an output amount of an asset and pair reserves, returns a required input amount of the other asset
	function getAmountIn(uint amountOut, uint reserveIn, uint reserveOut) internal pure returns (uint amountIn) {
		require(amountOut > 0, "UniswapV2Library: INSUFFICIENT_OUTPUT_AMOUNT");
		require(reserveIn > 0 && reserveOut > 0, "UniswapV2Library: INSUFFICIENT_LIQUIDITY");
		uint numerator = reserveIn.mul(amountOut).mul(1000);
		uint denominator = reserveOut.sub(amountOut).mul(997);
		amountIn = (numerator / denominator).add(1);
	}

	// performs chained getAmountOut calculations on any number of pairs
	function getAmountsOut(
		address factory,
		uint amountIn,
		address[] memory path
	) internal view returns (uint[] memory amounts) {
		require(path.length >= 2, "UniswapV2Library: INVALID_PATH");
		amounts = new uint[](path.length);
		amounts[0] = amountIn;
		for (uint i; i < path.length - 1; i++) {
			(uint reserveIn, uint reserveOut) = getReserves(factory, path[i], path[i + 1]);
			amounts[i + 1] = getAmountOut(amounts[i], reserveIn, reserveOut);
		}
	}

	// performs chained getAmountIn calculations on any number of pairs
	function getAmountsIn(
		address factory,
		uint amountOut,
		address[] memory path
	) internal view returns (uint[] memory amounts) {
		require(path.length >= 2, "UniswapV2Library: INVALID_PATH");
		amounts = new uint[](path.length);
		amounts[amounts.length - 1] = amountOut;
		for (uint i = path.length - 1; i > 0; i--) {
			(uint reserveIn, uint reserveOut) = getReserves(factory, path[i - 1], path[i]);
			amounts[i - 1] = getAmountIn(amounts[i], reserveIn, reserveOut);
		}
	}
}

// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.12;

import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
// import '@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol';
import "./Babylonian.sol";
import "./FullMath.sol";

import "./SafeMath.sol";
import "./UniswapV2Library.sol";

// library containing some math for dealing with the liquidity shares of a pair, e.g. computing their exact value
// in terms of the underlying tokens
library UniswapV2LiquidityMathLibrary {
	using SafeMath for uint256;

	// computes the direction and magnitude of the profit-maximizing trade
	function computeProfitMaximizingTrade(
		uint256 truePriceTokenA,
		uint256 truePriceTokenB,
		uint256 reserveA,
		uint256 reserveB
	) internal pure returns (bool aToB, uint256 amountIn) {
		aToB = FullMath.mulDiv(reserveA, truePriceTokenB, reserveB) < truePriceTokenA;

		uint256 invariant = reserveA.mul(reserveB);

		uint256 leftSide = Babylonian.sqrt(
			FullMath.mulDiv(
				invariant.mul(1000),
				aToB ? truePriceTokenA : truePriceTokenB,
				(aToB ? truePriceTokenB : truePriceTokenA).mul(997)
			)
		);
		uint256 rightSide = (aToB ? reserveA.mul(1000) : reserveB.mul(1000)) / 997;

		if (leftSide < rightSide) return (false, 0);

		// compute the amount that must be sent to move the price to the profit-maximizing price
		amountIn = leftSide.sub(rightSide);
	}

	// gets the reserves after an arbitrage moves the price to the profit-maximizing ratio given an externally observed true price
	function getReservesAfterArbitrage(
		address factory,
		address tokenA,
		address tokenB,
		uint256 truePriceTokenA,
		uint256 truePriceTokenB
	) internal view returns (uint256 reserveA, uint256 reserveB) {
		// first get reserves before the swap
		(reserveA, reserveB) = UniswapV2Library.getReserves(factory, tokenA, tokenB);

		require(reserveA > 0 && reserveB > 0, "UniswapV2ArbitrageLibrary: ZERO_PAIR_RESERVES");

		// then compute how much to swap to arb to the true price
		(bool aToB, uint256 amountIn) = computeProfitMaximizingTrade(
			truePriceTokenA,
			truePriceTokenB,
			reserveA,
			reserveB
		);

		if (amountIn == 0) {
			return (reserveA, reserveB);
		}

		// now affect the trade to the reserves
		if (aToB) {
			uint amountOut = UniswapV2Library.getAmountOut(amountIn, reserveA, reserveB);
			reserveA += amountIn;
			reserveB -= amountOut;
		} else {
			uint amountOut = UniswapV2Library.getAmountOut(amountIn, reserveB, reserveA);
			reserveB += amountIn;
			reserveA -= amountOut;
		}
	}

	// computes liquidity value given all the parameters of the pair
	function computeLiquidityValue(
		uint256 reservesA,
		uint256 reservesB,
		uint256 totalSupply,
		uint256 liquidityAmount,
		bool feeOn,
		uint kLast
	) internal pure returns (uint256 tokenAAmount, uint256 tokenBAmount) {
		if (feeOn && kLast > 0) {
			uint rootK = Babylonian.sqrt(reservesA.mul(reservesB));
			uint rootKLast = Babylonian.sqrt(kLast);
			if (rootK > rootKLast) {
				uint numerator1 = totalSupply;
				uint numerator2 = rootK.sub(rootKLast);
				uint denominator = rootK.mul(5).add(rootKLast);
				uint feeLiquidity = FullMath.mulDiv(numerator1, numerator2, denominator);
				totalSupply = totalSupply.add(feeLiquidity);
			}
		}
		return (reservesA.mul(liquidityAmount) / totalSupply, reservesB.mul(liquidityAmount) / totalSupply);
	}

	// get all current parameters from the pair and compute value of a liquidity amount
	// **note this is subject to manipulation, e.g. sandwich attacks**. prefer passing a manipulation resistant price to
	// #getLiquidityValueAfterArbitrageToPrice
	function getLiquidityValue(
		address factory,
		address tokenA,
		address tokenB,
		uint256 liquidityAmount
	) internal view returns (uint256 tokenAAmount, uint256 tokenBAmount) {
		(uint256 reservesA, uint256 reservesB) = UniswapV2Library.getReserves(factory, tokenA, tokenB);
		IUniswapV2Pair pair = IUniswapV2Pair(UniswapV2Library.pairFor(factory, tokenA, tokenB));
		bool feeOn = IUniswapV2Factory(factory).feeTo() != address(0);
		uint kLast = feeOn ? pair.kLast() : 0;
		uint totalSupply = pair.totalSupply();
		return computeLiquidityValue(reservesA, reservesB, totalSupply, liquidityAmount, feeOn, kLast);
	}

	// given two tokens, tokenA and tokenB, and their "true price", i.e. the observed ratio of value of token A to token B,
	// and a liquidity amount, returns the value of the liquidity in terms of tokenA and tokenB
	function getLiquidityValueAfterArbitrageToPrice(
		address factory,
		address tokenA,
		address tokenB,
		uint256 truePriceTokenA,
		uint256 truePriceTokenB,
		uint256 liquidityAmount
	) internal view returns (uint256 tokenAAmount, uint256 tokenBAmount) {
		bool feeOn = IUniswapV2Factory(factory).feeTo() != address(0);
		IUniswapV2Pair pair = IUniswapV2Pair(UniswapV2Library.pairFor(factory, tokenA, tokenB));
		uint kLast = feeOn ? pair.kLast() : 0;
		uint totalSupply = pair.totalSupply();

		// this also checks that totalSupply > 0
		require(totalSupply >= liquidityAmount && liquidityAmount > 0, "ComputeLiquidityValue: LIQUIDITY_AMOUNT");

		(uint reservesA, uint reservesB) = getReservesAfterArbitrage(
			factory,
			tokenA,
			tokenB,
			truePriceTokenA,
			truePriceTokenB
		);

		return computeLiquidityValue(reservesA, reservesB, totalSupply, liquidityAmount, feeOn, kLast);
	}
}

